document
  .getElementById("dadosPessoais")
  .addEventListener("submit", function (event) {
    event.preventDefault(); // Impede o envio do formulário
    preencherPDF(); // Chama a função para preencher o PDF
  });

async function preencherPDF() {
  // String base64 do PDF (substitua pelo seu PDF em base64)
  const pdfBase64 =
    "";

  // Converter base64 para array de bytes
  const pdfBytes = Uint8Array.from(atob(pdfBase64), (c) => c.charCodeAt(0));

  try {
    // Carregar o PDF
    const { PDFDocument } = PDFLib;
    const pdfDoc = await PDFDocument.load(pdfBytes);
    const form = pdfDoc.getForm();

    // Preencher os campos
    form.getTextField("nome").setText(document.getElementById("nome").value);
    form
      .getTextField("data_nascimento")
      .setText(document.getElementById("data_nascimento").value);
    form
      .getTextField("genero")
      .setText(document.getElementById("genero").value);
    form
      .getTextField("estado_civil")
      .setText(document.getElementById("estado_civil").value);
    form.getTextField("nif").setText(document.getElementById("nif").value);
    form.getTextField("ncc").setText(document.getElementById("ncc").value);
    form
      .getTextField("data_cc")
      .setText(document.getElementById("data_cc").value);
    form.getTextField("niss").setText(document.getElementById("niss").value);
    form
      .getTextField("morada")
      .setText(document.getElementById("morada").value);
    form.getTextField("cp").setText(document.getElementById("cp").value);
    form
      .getTextField("localidade")
      .setText(document.getElementById("localidade").value);
    form
      .getTextField("distrito")
      .setText(document.getElementById("distrito").value);
    form.getTextField("pais").setText(document.getElementById("pais").value);
    form
      .getTextField("telemovel")
      .setText(document.getElementById("telemovel").value);
    form
      .getTextField("telefone")
      .setText(document.getElementById("telefone").value);
    form.getTextField("email").setText(document.getElementById("email").value);
    form
      .getTextField("entidade_patronal")
      .setText(document.getElementById("entidade_patronal").value);
    form
      .getTextField("profissao")
      .setText(document.getElementById("profissao").value);
    form
      .getTextField("nif_da_empresa")
      .setText(document.getElementById("nif_da_empresa").value);
    form
      .getTextField("telemovel_empresa")
      .setText(document.getElementById("telemovel_empresa").value);
    form
      .getTextField("telefone_empresa")
      .setText(document.getElementById("telefone_empresa").value);
    form
      .getTextField("morada_empresa")
      .setText(document.getElementById("morada_empresa").value);

    // Salvar e baixar o PDF preenchido
    const updatedPdfBytes = await pdfDoc.save();
    const blob = new Blob([updatedPdfBytes], { type: "application/pdf" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "Ficha_Preenchida.pdf";
    link.click();
  } catch (error) {
    console.error("Erro ao preencher o PDF:", error);
    alert(
      "Ocorreu um erro ao preencher o PDF. Verifique o console para mais detalhes."
    );
  }
}

// Função para validar o nome completo
document.getElementById("nome").addEventListener("input", function (event) {
  let input = event.target;

  // Permite apenas letras e espaços, removendo números e caracteres especiais
  input.value = input.value.replace(/[^A-Za-zÀ-ÖØ-öø-ÿ\s]/g, "");

  // Formata para que cada palavra comece com maiúscula
  input.value = input.value.replace(/\b\w/g, function (char) {
    return char.toUpperCase();
  });

  // Remove espaços extras no início
  input.value = input.value.trimStart();
});

// Função para verificar se o campo está vazio
document.getElementById("nome").addEventListener("blur", function (event) {
  let input = event.target;
  let errorDiv = document.getElementById("nome-error"); // Área de erro para Nome Completo

  // Se o campo estiver vazio ou só tiver espaços, exibe a mensagem de erro
  if (input.value.trim() === "") {
    errorDiv.innerText = "O campo Nome Completo não pode estar vazio.";
    errorDiv.style.display = "block";
    input.focus();
  } else {
    errorDiv.style.display = "none"; // Se for válido, esconde a mensagem de erro
  }
});

